func CastGeoProvinceFromCli(c *cli.Context) *GeoProvinceEntity {
	template := &GeoProvinceEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		x := c.String("pid")
		template.ParentId = &x
	}
	if c.IsSet("name") {
		value := c.String("name")
		template.Name = &value
	}
	if c.IsSet("country-id") {
		value := c.String("country-id")
		template.CountryId = &value
	}
	return template
}
