func WidgetActionCreateFn(dto *WidgetEntity, query fireback.QueryDSL) (*WidgetEntity, *fireback.IError) {
	// 1. Validate always
	if iError := WidgetValidator(dto, false); iError != nil {
		return nil, iError
	}
	// 1.5 Sanitize the content coming of the front-end
	WidgetEntityPreSanitize(dto, query)
	// 2. Append the necessary information about user, workspace
	WidgetEntityBeforeCreateAppend(dto, query)
	// 3. Append the necessary translations, even if english
	WidgetPolyglotCreateHandler(dto, query)
	// 3.5. Create other entities if we want select from them
	WidgetRelationContentCreate(dto, query)
	// 4. Create the entity
	var dbref *gorm.DB = nil
	if query.Tx == nil {
		dbref = fireback.GetDbRef()
	} else {
		dbref = query.Tx
	}
	query.Tx = dbref
	err := dbref.Create(&dto).Error
	if err != nil {
		err := fireback.GormErrorToIError(err)
		return dto, err
	}
	// 5. Create sub entities, objects or arrays, association to other entities
	WidgetAssociationCreate(dto, query)
	// 6. Fire the event into system
	event.MustFire(WIDGET_EVENT_CREATED, event.M{
		"entity":    dto,
		"entityKey": fireback.GetTypeString(&WidgetEntity{}),
		"target":    "workspace",
		"unqiueId":  query.WorkspaceId,
	})
	return dto, nil
}
