func ProductEntityStream(q workspaces.QueryDSL) (chan []*ProductEntity, *workspaces.QueryResultMeta, error) {
	cn := make(chan []*ProductEntity)
	q.ItemsPerPage = 50
	q.StartIndex = 0
	_, qrm, err := ProductActions.Query(q)
	if err != nil {
		return nil, nil, err
	}
	go func() {
		defer close(cn)
		for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
			items, _, _ := ProductActions.Query(q)
			i += q.ItemsPerPage
			q.StartIndex = i
			cn <- items
		}
	}()
	return cn, qrm, nil
}

