func (x *TagEntity) FromCli(c *cli.Context) *TagEntity {
	return CastTagFromCli(c)
}
func CastTagFromCli(c *cli.Context) *TagEntity {
	template := &TagEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = workspaces.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("name") {
		template.Name = c.String("name")
	}
	if c.IsSet("description") {
		template.Description = workspaces.NewStringAutoNull(c.String("description"))
	}
	return template
}
