package org.example
import okhttp3.*
import kotlinx.coroutines.*
import kotlinx.coroutines.flow.*

fun main() = runBlocking {
    val (messages, connect) = useEventBusSubscription()
    messages.onEach { println("Got: $it") }.launchIn(this)
    connect()
    delay(10_000)
}
