func FileEntityStream(q workspaces.QueryDSL) (chan []*FileEntity, *workspaces.QueryResultMeta, error) {
	cn := make(chan []*FileEntity)
	q.ItemsPerPage = 50
	q.StartIndex = 0
	_, qrm, err := FileActions.Query(q)
	if err != nil {
		return nil, nil, err
	}
	go func() {
		defer close(cn)
		for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
			items, _, _ := FileActions.Query(q)
			i += q.ItemsPerPage
			q.StartIndex = i
			cn <- items
		}
	}()
	return cn, qrm, nil
}

