var AppendFileRouter = func(r *[]workspaces.Module3Action) {}

func GetFileModule3Actions() []workspaces.Module3Action {
	routes := []workspaces.Module3Action{
		FILE_ACTION_QUERY,
		FILE_ACTION_EXPORT,
		FILE_ACTION_GET_ONE,
		FILE_ACTION_POST_ONE,
		FILE_ACTION_PATCH,
		FILE_ACTION_PATCH_BULK,
		FILE_ACTION_DELETE,
		FILE_VARIATIONS_ACTION_PATCH,
		FILE_VARIATIONS_ACTION_GET,
		FILE_VARIATIONS_ACTION_POST,
	}
	// Append user defined functions
	AppendFileRouter(&routes)
	return routes
}

