func CastPassportFromCli(c *cli.Context) *PassportEntity {
	template := &PassportEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = fireback.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("third-party-verifier") {
		template.ThirdPartyVerifier = c.String("third-party-verifier")
	}
	if c.IsSet("type") {
		template.Type = c.String("type")
	}
	if c.IsSet("user-id") {
		template.UserId = fireback.NewStringAutoNull(c.String("user-id"))
	}
	if c.IsSet("value") {
		template.Value = c.String("value")
	}
	if c.IsSet("totp-secret") {
		template.TotpSecret = c.String("totp-secret")
	}
	if c.IsSet("password") {
		template.Password = c.String("password")
	}
	if c.IsSet("access-token") {
		template.AccessToken = c.String("access-token")
	}
	return template
}
