func CastUserFromCli(c *cli.Context) *UserEntity {
	template := &UserEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = fireback.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("first-name") {
		template.FirstName = c.String("first-name")
	}
	if c.IsSet("last-name") {
		template.LastName = c.String("last-name")
	}
	if c.IsSet("photo") {
		template.Photo = c.String("photo")
	}
	if c.IsSet("title") {
		template.Title = c.String("title")
	}
	if c.IsSet("birth-date") {
		value := c.String("birth-date")
		template.BirthDate.Scan(value)
	}
	if c.IsSet("avatar") {
		template.Avatar = c.String("avatar")
	}
	if c.IsSet("last-ip-address") {
		template.LastIpAddress = c.String("last-ip-address")
	}
	return template
}
