var AppendUserRouter = func(r *[]fireback.Module3Action) {}

func GetUserModule3Actions() []fireback.Module3Action {
	routes := []fireback.Module3Action{
		USER_ACTION_QUERY,
		USER_ACTION_EXPORT,
		USER_ACTION_GET_ONE,
		USER_ACTION_POST_ONE,
		USER_ACTION_PATCH,
		USER_ACTION_PATCH_BULK,
		USER_ACTION_DELETE,
	}
	// Append user defined functions
	AppendUserRouter(&routes)
	return routes
}

