func UserEntityStream(q workspaces.QueryDSL) (chan []*UserEntity, *workspaces.QueryResultMeta, error) {
	cn := make(chan []*UserEntity)
	q.ItemsPerPage = 50
	q.StartIndex = 0
	_, qrm, err := UserActions.Query(q)
	if err != nil {
		return nil, nil, err
	}
	go func() {
		defer close(cn)
		for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
			items, _, _ := UserActions.Query(q)
			i += q.ItemsPerPage
			q.StartIndex = i
			cn <- items
		}
	}()
	return cn, qrm, nil
}

