func UserMultiInsertFn(dtos []*UserEntity, query workspaces.QueryDSL) ([]*UserEntity, *workspaces.IError) {
	if len(dtos) > 0 {
		for index := range dtos {
			UserEntityPreSanitize(dtos[index], query)
			UserEntityBeforeCreateAppend(dtos[index], query)
		}
		var dbref *gorm.DB = nil
		if query.Tx == nil {
			dbref = workspaces.GetDbRef()
		} else {
			dbref = query.Tx
		}
		query.Tx = dbref
		err := dbref.Create(&dtos).Error
		if err != nil {
			return nil, workspaces.GormErrorToIError(err)
		}
	}
	return dtos, nil
}
func UserActionBatchCreateFn(dtos []*UserEntity, query workspaces.QueryDSL) ([]*UserEntity, *workspaces.IError) {
	if dtos != nil && len(dtos) > 0 {
		items := []*UserEntity{}
		for _, item := range dtos {
			s, err := UserActions.Create(item, query)
			if err != nil {
				return nil, err
			}
			items = append(items, s)
		}
		return items, nil
	}
	return dtos, nil
}
