func UserActionCreateFn(dto *UserEntity, query workspaces.QueryDSL) (*UserEntity, *workspaces.IError) {
	// 1. Validate always
	if iError := UserValidator(dto, false); iError != nil {
		return nil, iError
	}
	// 1.5 Sanitize the content coming of the front-end
	UserEntityPreSanitize(dto, query)
	// 2. Append the necessary information about user, workspace
	UserEntityBeforeCreateAppend(dto, query)
	// 3. Create other entities if we want select from them
	UserRelationContentCreate(dto, query)
	// 4. Create the entity
	var dbref *gorm.DB = nil
	if query.Tx == nil {
		dbref = workspaces.GetDbRef()
	} else {
		dbref = query.Tx
	}
	query.Tx = dbref
	err := dbref.Create(&dto).Error
	if err != nil {
		err := workspaces.GormErrorToIError(err)
		return nil, err
	}
	// 5. Create sub entities, objects or arrays, association to other entities
	UserAssociationCreate(dto, query)
	// 6. Fire the event into system
	event.MustFire(USER_EVENT_CREATED, event.M{
		"entity":    dto,
		"entityKey": workspaces.GetTypeString(&UserEntity{}),
		"target":    "workspace",
		"unqiueId":  query.WorkspaceId,
	})
	return dto, nil
}
