func InvoiceActionCreateFn(dto *InvoiceEntity, query fireback.QueryDSL) (*InvoiceEntity, *fireback.IError) {
	// 1. Validate always
	if iError := InvoiceValidator(dto, false); iError != nil {
		return nil, iError
	}
	// 1.5 Sanitize the content coming of the front-end
	InvoiceEntityPreSanitize(dto, query)
	// 2. Append the necessary information about user, workspace
	InvoiceEntityBeforeCreateAppend(dto, query)
	// 3. Create other entities if we want select from them
	InvoiceRelationContentCreate(dto, query)
	// 4. Create the entity
	var dbref *gorm.DB = nil
	if query.Tx == nil {
		dbref = fireback.GetDbRef()
	} else {
		dbref = query.Tx
	}
	query.Tx = dbref
	err := dbref.Create(&dto).Error
	if err != nil {
		err := fireback.GormErrorToIError(err)
		return nil, err
	}
	// 5. Create sub entities, objects or arrays, association to other entities
	InvoiceAssociationCreate(dto, query)
	// 6. Fire the event into system
	actionEvent, eventErr := NewInvoiceCreatedEvent(dto, &query)
	if actionEvent != nil && eventErr == nil {
		fireback.GetEventBusInstance().FireEvent(query, *actionEvent)
	} else {
		log.Default().Panicln("Creating event has failed for %v", dto)
	}
	/*
		event.MustFire(INVOICE_EVENT_CREATED, event.M{
			"entity":   dto,
			"entityKey": fireback.GetTypeString(&InvoiceEntity{}),
			"target":   "workspace",
			"unqiueId": query.WorkspaceId,
		})
	*/
	return dto, nil
}
