func InvoiceActionWipeClean(query fireback.QueryDSL) (int64, error) {
	var err error
	var count int64 = 0
	{
		subCount, subErr := fireback.WipeCleanEntity[InvoiceEntity]()
		if subErr != nil {
			fmt.Println("Error while wiping 'InvoiceEntity'", subErr)
			return count, subErr
		} else {
			count += subCount
		}
	}
	return count, err
}
func InvoiceActionBulkUpdate(
	query fireback.QueryDSL, dto *fireback.BulkRecordRequest[InvoiceEntity]) (
	*fireback.BulkRecordRequest[InvoiceEntity], *fireback.IError,
) {
	result := []*InvoiceEntity{}
	err := fireback.GetDbRef().Transaction(func(tx *gorm.DB) error {
		query.Tx = tx
		for _, record := range dto.Records {
			item, err := InvoiceActions.Update(query, record)
			if err != nil {
				return err
			} else {
				result = append(result, item)
			}
		}
		return nil
	})
	if err == nil {
		return dto, nil
	}
	return nil, err.(*fireback.IError)
}
