func CastInvoiceFromCli(c *cli.Context) *InvoiceEntity {
	template := &InvoiceEntity{}
	fireback.HandleXsrc(c, template)
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = fireback.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("title") {
		template.Title = c.String("title")
	}
	if c.IsSet("amount") {
		template.Amount = fireback.NewMoneyAutoNull(c.String("amount"))
	}
	if c.IsSet("notification-key") {
		template.NotificationKey = c.String("notification-key")
	}
	if c.IsSet("redirect-after-success") {
		template.RedirectAfterSuccess = c.String("redirect-after-success")
	}
	if c.IsSet("final-status") {
		template.FinalStatus = c.String("final-status")
	}
	return template
}
