var AppendInvoiceRouter = func(r *[]fireback.Module3Action) {}

func GetInvoiceModule3Actions() []fireback.Module3Action {
	routes := []fireback.Module3Action{
		INVOICE_ACTION_QUERY,
		INVOICE_ACTION_EXPORT,
		INVOICE_ACTION_GET_ONE,
		INVOICE_ACTION_POST_ONE,
		INVOICE_ACTION_PATCH,
		INVOICE_ACTION_PATCH_BULK,
		INVOICE_ACTION_DELETE,
	}
	// Append user defined functions
	AppendInvoiceRouter(&routes)
	return routes
}

