func ContentEntityStream(q fireback.QueryDSL) (chan []*ContentEntity, *fireback.QueryResultMeta, error) {
	cn := make(chan []*ContentEntity)
	q.ItemsPerPage = 50
	q.StartIndex = 0
	_, qrm, err := ContentActions.Query(q)
	if err != nil {
		return nil, nil, err
	}
	go func() {
		defer close(cn)
		for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
			items, _, _ := ContentActions.Query(q)
			i += q.ItemsPerPage
			q.StartIndex = i
			cn <- items
		}
	}()
	return cn, qrm, nil
}

