func ContentActionCreateFn(dto *ContentEntity, query fireback.QueryDSL) (*ContentEntity, *fireback.IError) {
	// 1. Validate always
	if iError := ContentValidator(dto, false); iError != nil {
		return nil, iError
	}
	// 1.5 Sanitize the content coming of the front-end
	ContentEntityPreSanitize(dto, query)
	// 2. Append the necessary information about user, workspace
	ContentEntityBeforeCreateAppend(dto, query)
	// 3. Create other entities if we want select from them
	ContentRelationContentCreate(dto, query)
	// 4. Create the entity
	var dbref *gorm.DB = nil
	if query.Tx == nil {
		dbref = fireback.GetDbRef()
	} else {
		dbref = query.Tx
	}
	query.Tx = dbref
	err := dbref.Create(&dto).Error
	if err != nil {
		err := fireback.GormErrorToIError(err)
		return nil, err
	}
	// 5. Create sub entities, objects or arrays, association to other entities
	ContentAssociationCreate(dto, query)
	// 6. Fire the event into system
	actionEvent, eventErr := NewContentCreatedEvent(dto, &query)
	if actionEvent != nil && eventErr == nil {
		fireback.GetEventBusInstance().FireEvent(query, *actionEvent)
	} else {
		log.Default().Panicln("Creating event has failed for %v", dto)
	}
	/*
		event.MustFire(CONTENT_EVENT_CREATED, event.M{
			"entity":   dto,
			"entityKey": fireback.GetTypeString(&ContentEntity{}),
			"target":   "workspace",
			"unqiueId": query.WorkspaceId,
		})
	*/
	return dto, nil
}
