func ContentActionBulkUpdate(
	query fireback.QueryDSL, dto *fireback.BulkRecordRequest[ContentEntity]) (
	*fireback.BulkRecordRequest[ContentEntity], *fireback.IError,
) {
	result := []*ContentEntity{}
	err := fireback.GetDbRef().Transaction(func(tx *gorm.DB) error {
		query.Tx = tx
		for _, record := range dto.Records {
			item, err := ContentActions.Update(query, record)
			if err != nil {
				return err
			} else {
				result = append(result, item)
			}
		}
		return nil
	})
	if err == nil {
		return dto, nil
	}
	return nil, err.(*fireback.IError)
}
