func CastContentFromCli(c *cli.Context) *ContentEntity {
	template := &ContentEntity{}
	fireback.HandleXsrc(c, template)
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = fireback.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("title") {
		template.Title = c.String("title")
	}
	if c.IsSet("excerpt") {
		template.Excerpt = c.String("excerpt")
	}
	if c.IsSet("content-type") {
		template.ContentType = c.String("content-type")
	}
	return template
}
