func CastPassportMethodFromCli(c *cli.Context) *PassportMethodEntity {
	template := &PassportMethodEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		x := c.String("pid")
		template.ParentId = &x
	}
	if c.IsSet("name") {
		value := c.String("name")
		template.Name = &value
	}
	if c.IsSet("type") {
		value := c.String("type")
		template.Type = &value
	}
	if c.IsSet("region") {
		value := c.String("region")
		template.Region = &value
	}
	return template
}
