func CastTableViewSizingFromCli(c *cli.Context) *TableViewSizingEntity {
	template := &TableViewSizingEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("table-name") {
		template.TableName = c.String("table-name")
	}
	if c.IsSet("sizes") {
		template.Sizes = c.String("sizes")
	}
	return template
}
