func UserEntityStream(q QueryDSL) (chan []*UserEntity, *QueryResultMeta, error) {
	cn := make(chan []*UserEntity)
	q.ItemsPerPage = 50
	q.StartIndex = 0
	_, qrm, err := UserActionQuery(q)
	if err != nil {
		return nil, nil, err
	}
	go func() {
		for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
			items, _, _ := UserActionQuery(q)
			i += q.ItemsPerPage
			q.StartIndex = i
			cn <- items
		}
	}()
	return cn, qrm, nil
}

