func CastUserProfileFromCli(c *cli.Context) *UserProfileEntity {
	template := &UserProfileEntity{}
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("first-name") {
		template.FirstName = c.String("first-name")
	}
	if c.IsSet("last-name") {
		template.LastName = c.String("last-name")
	}
	return template
}
